
import sys
def minWinSubstring(arr,t):
        i,j=0,0
        map={}
        index=[]
        minVal,finalMin=sys.maxsize,sys.maxsize
        for ele in t :
            if ele not in map:
                map[ele] = 1
            else:
                map[ele] += 1
        cnt=len(map)
        while j < len(arr):
            if arr[j] in map:
                map[arr[j]] -= 1
                if map[arr[j]] == 0:
                    cnt -= 1
            while cnt == 0:
                minVal = j-i+1
                if minVal < finalMin:
                    finalMin = minVal
                    index = []
                    index.append(i)
                    index.append(j)
                if arr[i] in map:
                    map[arr[i]] += 1
                    if map[arr[i]] == 1:
                        cnt += 1

                i += 1
            j += 1

        if len(index):
            return arr[index[0]:index[1]+1]
        else:
            return []


